import React, { Component } from 'react'

import CiteService from '../../../../service/cite.service'
import QueryService from '../../../../service/query.service'
import PetService from '../../../../service/pet.service'



import CiteCard from '../../cites/citesCard/CiteCard'
import QueryCard from '../../query/queryCard/QueryCard'
import CiteForm from '../../cites/citesForm/CiteForm'

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Figure from 'react-bootstrap/Figure'
import FigureImage from 'react-bootstrap/FigureImage'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Toast from 'react-bootstrap/Toast'
import Modal from 'react-bootstrap/Modal'

import { Link } from 'react-router-dom'


class DashboardClient extends Component {

    constructor(props) {
        super(props)
        this.state = { 
            userInfo: {
                pets: ""
            },
            toast: {
                show: false,
                text: ''
            },
            modalShow: false

        }
        this.citeService = new CiteService()
        this.petService = new PetService()
    }

    handleModal = visible => this.setState({ modalShow: visible })
    handletoast = (visible, text = '') => {
        const toastCopy = { ...this.state.toast }
        toastCopy.show = visible
        toastCopy.text = text
        this.setState({ toast: toastCopy })
    }

    // getCitesInfo = () => {
    //     let petId 
    //     this.props.loggedInUser.pets && this.props.loggedInUser.pets.map(pet => {
    //          console.log(`Este es el ide de los pets?? ${pet._id}`)
    //          petId = pet._id

    //     return petId})
    //     console.log(`Este deberia ser petId${petId}`)
    //          this.citeService.citesFromPet(petId)
    //          .then(response => this.setState({ cites: response.data }))
    //     .catch(err => console.log(err))
    // }

    getPetsInfo = () => {
        this.petService.queryFromClient(this.props.loggedInUser._id)
        .then(response => this.setState({ pets: response.data }))
        .catch(err => console.log(err))
    }


    componentDidMount = () => {
        // this.getCitesInfo()
        this.getQueryInfo()
    }

    finishQueryPost = () => {
        this.getQueryInfo()
        this.handleModal(false)
        this.handletoast(true, 'Consulta creada en BBDD')
    }


    render() {

        // {this.props.loggedInUser.pets && this.props.loggedInUser.pets.map(cite =>  console.log(cite._id))}
        console.log(this.props.loggedInUser.pets)
        // console.log(this.state.cites)
        // console.log(this.state.querys)

        return (
            <>
                <Container as="section" >
                    <Row>
                        <Col>
                            {/* utilizar con estos botones un renderizado condicional
                         segun el role del usuario */}
                         <Button onClick={() => this.handleModal(true)} variant="dark" style={{ marginBottom: '20px' }}>Crear nueva consulta</Button>

                        </Col>
                        <Col>
                            <Button></Button>

                        </Col>
                        <Col>
                            <Button></Button>
                        </Col>

                    </Row>
                </Container>

                <Container as="section" >

                <h1>Citas</h1>


                    {this.state.cites && this.state.cites.map(cite =>  <CiteCard key={cite._id} {...cite} />)}


                </Container>

                <Container as="section" >

                <h1>Consultas</h1>


                    {this.state.querys && this.state.querys.map(query => <QueryCard key={query._id} {...query} />)}


                </Container>

                <Modal show={this.state.modalShow} onHide={() => this.handleModal(false)}>
                    <Modal.Body>
                        <CiteForm finishCitePost={this.finishCiteCreate} closeModal={() => this.handleModal(false)} />
                    </Modal.Body>
                </Modal>

                <Toast onClose={() => this.handletoast(false)} show={this.state.toast.show} delay={3000} >
                    <Toast.Header>
                        <img src="holder.js/20x20?text=%20" className="rounded mr-2" alt="" />
                        <strong className="mr-auto">Mensaje</strong>
                    </Toast.Header>
                    <Toast.Body>{this.state.toast.text}</Toast.Body>
                </Toast>
            </>

        )
    }
}

export default DashboardClient
