import React, { Component } from 'react'
import './QueryCard.css'


import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'

import { Link } from 'react-router-dom'

class QueryCard extends Component {

  constructor(props) {
    super(props)
    this.state = {

      toast: {
        show: false,
        text: ''
      },
      modalShow: false
    }

  }



  render() {
    return (
      <>
        <Card>
          <Card.Header as="h5">Consulta: {this.props.status}</Card.Header>
          <Card.Body>
            <Card.Title>{this.props.subject}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">{this.props.date}</Card.Subtitle>
            <Card.Text>
              {this.props.description}
            </Card.Text>
            <Button variant="dark"><Link to="">Detalles</Link></Button>
          </Card.Body>
        </Card>

        {/* <Modal show={this.state.modalShow} onHide={() => this.handleModal(false)}>
          <Modal.Body>
            <CiteForm finishCitePost={this.finishCiteCreate} hospitalID={this.props.loggedInUser._id} pets={this.props.loggedInUser.pets} closeModal={() => this.handleModal(false)} />
          </Modal.Body>
        </Modal>

        <Toast onClose={() => this.handletoast(false)} show={this.state.toast.show} delay={3000} >
          <Toast.Header>
            <strong className="mr-auto">Mensaje</strong>
          </Toast.Header>
          <Toast.Body>{this.state.toast.text}</Toast.Body>
        </Toast> */}
      </>
    )
  }
}

export default QueryCard
