import React, { Component } from 'react'
import './QueryCard.css'


import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'

import { Link } from 'react-router-dom'

class QueryCard extends Component {

  constructor(props) {
        super(props)
        this.state = {
            petInfo: {
                cites: "",
                querys: "",
                vetHospital: ""
            },
            toast: {
                show: false,
                text: ''
            },
            modalShow: false
        }
        this.citeService = new CiteService()
        this.queryService = new QueryService()

    }
    render() {
  return (
    <>
      <Card>
        <Card.Header as="h5">Consulta</Card.Header>
        <Card.Body>
          <Card.Title>{props.subject}</Card.Title>
          <Card.Subtitle className="mb-2 text-muted">{props.date}</Card.Subtitle>
          <Card.Text>
            {props.description}
          </Card.Text>
          {/* <Button variant="dark"><Link to="">Detalles</Link></Button> */}
        </Card.Body>
      </Card>

      <Modal show={this.state.modalShow} onHide={() => this.handleModal(false)}>
        <Modal.Body>
          <CiteForm finishCitePost={this.finishCiteCreate} hospitalID={this.props.loggedInUser._id} pets={this.props.loggedInUser.pets} closeModal={() => this.handleModal(false)} />
        </Modal.Body>
      </Modal>

      <Toast onClose={() => this.handletoast(false)} show={this.state.toast.show} delay={3000} >
        <Toast.Header>
          <strong className="mr-auto">Mensaje</strong>
        </Toast.Header>
        <Toast.Body>{this.state.toast.text}</Toast.Body>
      </Toast>
    </>
  )
}
}

export default QueryCard
