import React, { Component } from 'react'
import './QueryCard.css'

import CiteService from '../../../../service/cite.service'


import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'

import { Link } from 'react-router-dom'

class QueryCard extends Component {

  constructor(props) {
    super(props)
    this.state = {

      responseToQuery: {
        subject: '',
        description: '',
        pet: '',
        vetHospital: ''
      },
      toast: {
        show: false,
        text: ''
      },
      modalShow: false
    }
    this.citeService = new CiteService()

  }

  handleModal = visible => this.setState({ modalShow: visible })

  handleInputChange = e => {
    const { name, value } = e.target

    this.setState({
      [name]: value
    })
  }


  handleSubmit = e => {
    e.preventDefault()
    let newQuery = { ...this.state }
    newQuery.pet = this.props.petId
    this.queryService.queryCreate(newQuery)
      .then(() => this.props.finishQueryCreate())
      .catch(err => console.log(err))
  }



  render() {
    console.log(this.props)
    return (
      <>
        <Card>
          <Card.Header as="h5">Consulta: {this.props.status}</Card.Header>
          <Card.Body>
            <Card.Title>{this.props.subject}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">{this.props.date}</Card.Subtitle>
            <Card.Text>
              {this.props.description}
            </Card.Text>
            <Button variant="dark">Responder</Button>
          </Card.Body>
        </Card>

        <Modal show={this.state.modalShow} onHide={() => this.handleModal(false)}>
          <Modal.Body>
            <CiteForm finishCitePost={this.finishCiteCreate} hospitalID={this.props.vetHospital} pets={this.props.loggedInUser.pets} closeModal={() => this.handleModal(false)} />
          </Modal.Body>
        </Modal>

      </>
    )
  }
}

export default QueryCard
