import React, {
    Component
} from 'react'

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'

import {
    Link
} from 'react-router-dom'

class HospitalProfile extends Component {

    constructor(props) {
        super(props)
        this.state = {
            upDateProfileInfo: {
                username: String,
                surname: String,
                email: String,
                password: String,
                profilePicPath: String,
                address: String,
                phoneNumber: String,
            },
            errorMessage: ''
        }
        this.authService = new AuthService()
    }

    handleInputChange = e => {

        let upDateProfileInfoCopy = {
            ...this.state.upDateProfileInfo
        }
        const {
            name,
            value
        } = e.target
        upDateProfileInfoCopy = {
            ...upDateProfileInfoCopy,
            [name]: value
        }

        this.setState({
            upDateProfileInfo: upDateProfileInfoCopy
        })
    }

    handleSubmit = e => {
        e.preventDefault()
        this.authService.signup(this.state.signUpInfo)
            .then(response => {
                this.props.setTheUser(response.data)
                this.props.history.push('/')
            })
            .catch(err => {
                err.response.status === 400 && this.setState({
                    errorMessage: err.response.data.message
                })
            })
    }

    render() {
        return (<
            Container >

            <
            Row >
                <
            Col md={
                        {
                            span: 4,
                            offset: 4
                        }
                    } >

                    <
            h3 > Registro de usuario < /h3> <
            hr > < /hr> <
            Form onSubmit={
                                    this.handleSubmit
                                } >

                                <
            Form.Group controlId="name" >
                                    <
            Form.Label > Usuario < /Form.Label> <
                                            Form.Control name="username"
                                            type="text"
                                            value={
                                                this.state.username
                                            }
                                            onChange={
                                                this.handleInputChange
                                            }
                                        /> <
            /Form.Group>

            <
            Form.Group controlId="email" >
                                            <
            Form.Label > Email < /Form.Label> <
                                                    Form.Control name="email"
                                                    type="email"
                                                    value={
                                                        this.state.email
                                                    }
                                                    onChange={
                                                        this.handleInputChange
                                                    }
                                                /> <
            /Form.Group>

            <
            Form.Group controlId="pwd" >
                                                    <
            Form.Label > Contraseña < /Form.Label> <
                                                            Form.Control name="password"
                                                            type="password"
                                                            value={
                                                                this.state.password
                                                            }
                                                            onChange={
                                                                this.handleInputChange
                                                            }
                                                        /> <
            /Form.Group>

            {
                                                            /* <p
                                                                                            className='error-message'
                                                                                            style={{ display: this.state.errorMessage ? 'block' : 'none' }}
                                                                                        >{this.state.errorMessage}</p> */
                                                        }

                                                        <
            Button variant="dark"
                                                            type="submit" > Registrarme < /Button> <
            /Form>

            <
            p > < small > ¿Ya tienes cuenta ? < Link to="/login" > Inicia sesión < /Link></small > < /p>

            <
            /Col> <
            /Row>

            <
            /Container>
        )
    }
}

export default HospitalProfile
