import React, {
    Component
} from 'react'

import CiteService from '../../../../service/cite.service'
import QueryService from '../../../../service/query.service'

import QueryForm from '../../query/queryForm/QueryForm'

import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import ListGroup from 'react-bootstrap/ListGroup'
import Modal from 'react-bootstrap/Modal'
import Toast from 'react-bootstrap/Toast'


class PetCardClient extends Component {

    constructor(props) {
        super(props)
        this.state = {

            petInfo: {
                id: this.props._id,
                petPicPath: this.props.petPicPath,
                name: this.props.name,
                species: this.props.species,
                breed: this.props.breed,
                vetHospital: this.props.vetHospital

            },

            cites: "",
            querys: "",

            toast: {
                show: false,
                text: ''
            },
            modalShow: false
        }
        this.citeService = new CiteService()
        this.queryService = new QueryService()

    }

    handleModal = visible => this.setState({
        modalShow: visible
    })
    handletoast = (visible, text = '') => {
        const toastCopy = {
            ...this.state.toast
        }
        toastCopy.show = visible
        toastCopy.text = text
        this.setState({
            toast: toastCopy
        })
    }

    getQuerysInfo = () => {
        this.queryService.queryFromPet(this.props._id)
            .then(response => this.setState({
                querys: response.data
            }))
            .catch(err => console.log(err))
    }

    getCitesInfo = () => {
        this.citeService.citesFromPet(this.props._id)
            .then(response => this.setState({
                cites: response.data
            }))
            .catch(err => console.log(err))
    }

    componentDidMount = () => {
        this.getQuerysInfo()
        this.getCitesInfo()
    }

    finishQueryPost = () => {
        this.getQuerysInfo()
        this.handleModal(false)
        this.handletoast(true, 'Consulta creada en BBDD')
    }



    render() {

        return ( <
            >
            <
            Container as = "section" >


            <
            Row >
            <
            Col as = "article" >
            <
            Card >
            <
            Card.Img variant = "top"
            src = {
                this.state.petInfo.petPicPath
            }
            />

            <
            Card.Header as = "h5" > {
                this.state.petInfo.name
            } < /Card.Header> <
            ListGroup className = "list-group-flush" >
            <
            ListGroup.Item > {
                this.state.petInfo.species
            } < /ListGroup.Item> <
            ListGroup.Item > {
                this.state.petInfo.breed
            } < /ListGroup.Item> <
            /ListGroup>

            <
            h4 > Consultas < /h4>

            {
                this.state.querys && this.state.querys.map(query => {

                    console.log(query)

                    return ( < Card key = {
                            query._id
                        } >
                        <
                        Card.Body >
                        <
                        Card.Title > {
                            query.subject
                        } < /Card.Title> <
                        Card.Subtitle > {
                            query.date
                        } < /Card.Subtitle> <
                        Card.Text > {
                            query.description
                        } < /Card.Text> <
                        /Card.Body> <
                        Card.Footer > {
                            query.citeHospital
                        } < /Card.Footer></Card > )
                })
            }

            <
            h4 > Citas < /h4>

            {
                this.state.cites && this.state.cites.map(cite => {

                            console.log(cite)

                            return ( < Card.Body key = {
                                    cite._id
                                } >
                                <
                                Card.Title > {
                                    cite.subject
                                } < /Card.Title> <
                                Card.Subtitle > {
                                    cite.date
                                } < /Card.Subtitle> <
                                Card.Subtitle > {
                                    cite.description
                                } < /Card.Subtitle> <
                                /Card.Body>)
                            })
                    }


                    <
                    Card.Footer >
                    <
                    Button onClick = {
                        () => this.handleModal(true)
                    }
                variant = "dark" > Crear Consulta para {
                        this.state.petInfo.name
                    } < /Button>

                    <
                    /Card.Footer> <
                    /Card>

                    <
                    /Col>

                    <
                    /Row>

                    <
                    /Container>

                    <
                    Modal show = {
                        this.state.modalShow
                    }
                onHide = {
                        () => this.handleModal(false)
                    } >
                    <
                    Modal.Body >
                    <
                    QueryForm finishQueryCreate = {
                        this.finishQueryPost
                    }
                petId = {
                    this.state.petInfo.id
                }
                hospitalArr = {
                    this.state.petInfo.vetHospital
                }
                closeModal = {
                    () => this.handleModal(false)
                }
                /> <
                /Modal.Body> <
                /Modal>

                <
                Toast onClose = {
                    () => this.handletoast(false)
                }
                show = {
                    this.state.toast.show
                }
                delay = {
                        3000
                    } >
                    <
                    Toast.Header >
                    <
                    strong className = "mr-auto" > Mensaje < /strong> <
                    /Toast.Header> <
                    Toast.Body > {
                        this.state.toast.text
                    } < /Toast.Body> <
                    /Toast>

                    <
                    />
            )
        }
    }

    export default PetCardClient
