import React, { Component } from 'react'
import './QueryCard.css'


import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'

import { Link } from 'react-router-dom'

class QueryCard extends Component {

  constructor(props) {
    super(props)
    this.state = {

      toast: {
        show: false,
        text: ''
      },
      modalShow: false
    }

  }

  handleModal = visible => this.setState({ modalShow: visible })




  render() {
    return (
      <>
        <Card>
          <Card.Header as="h5">Consulta: {this.props.status}</Card.Header>
          <Card.Body>
            <Card.Title>{this.props.subject}</Card.Title>
            <Card.Subtitle className="mb-2 text-muted">{this.props.date}</Card.Subtitle>
            <Card.Text>
              {this.props.description}
            </Card.Text>
            <Button variant="dark">Responder</Button>
          </Card.Body>
        </Card>

        <Modal show={this.state.modalShow} onHide={() => this.handleModal(false)}>
          <Modal.Body>
            <CiteForm finishCitePost={this.finishCiteCreate} hospitalID={this.props._id} pets={this.props.loggedInUser.pets} closeModal={() => this.handleModal(false)} />
          </Modal.Body>
        </Modal>

      </>
    )
  }
}

export default QueryCard
