const user = {
  roles: ['user'],
  rights: ['can_view_articles']
};

const admin = {
  roles: ['user', 'admin'],
  rights: ['can_view_articles', 'can_view_users']
};

export const hasRole = (user, roles) =>
  roles.some(role => user.roles.includes(role));
