import React, { Component } from 'react'
import CiteService from '../../../../service/cite.service'

import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'


class CiteResponseForm extends Component {

    constructor(props) {
        super(props)
        this.state = {
            responseCite: {
                subject: '',
                description: '',
                vetHospital: '',
                pet: ''
            }
        }
        this.citeService = new CiteService()
    }



    handleInputChange = e => {
        const { name, value } = e.target

        this.setState({
            [name]: value
        })
    }

    handleSubmit = e => {
        e.preventDefault()
        let newResponseCite = { ...this.state.responseCite }
        newResponseCite.vetHospital = this.props.hospitalID
        newResponseCite.pet = this.props.pet
        this.citeService.createCiteResponse(newResponseCite)
            .then(() => this.props.finishCiteResponsePost())
            .catch(err => console.log(err))
    }


    render() {
        return (
            <Container>

                <h1>Resopuesta a la Consulta</h1>
                <hr></hr>
                <Form onSubmit={this.handleSubmit}>
                    <Form.Group controlId="asunto">
                        <Form.Label>Asunto</Form.Label>
                        <Form.Control plaintext readOnly defaultValue={this.state.subject} />
                        <Form.Control name="subject" type="text" value={this.state.subject}  />
                    </Form.Group>
                    <Form.Group controlId="desc">
                        <Form.Label>Descripcion</Form.Label>
                        <Form.Control name="description" type="text" value={this.state.description} onChange={this.handleInputChange} />
                    </Form.Group>
                    <Form.Group controlId="date">
                        <Form.Label>Dia de la cita</Form.Label>
                        <Form.Control name="date" type="date" value={this.state.date} onChange={this.handleInputChange} />
                    </Form.Group>
                    {/* <Form.Group controlId="img">
                        <Form.Label>Imagen (URL)</Form.Label>
                        <Form.Control name="imageUrl" type="text" value={this.state.imageUrl} onChange={this.handleInputChange} />
                    </Form.Group> */}
                    <Button variant="dark" onClick={() => this.props.closeModal()} style={{ marginRight: '10px' }}>Cerrar</Button>
                    <Button variant="dark" type="submit">Nueva cita</Button>
                </Form>
            </Container>
        )
    }
}

export default CiteResponseForm
